import React from "react";
import classes from "./PostcodeForm.module.css"
import { History } from "history";
import { useFetch } from "../../hooks/useFetch";

export const PostcodeForm = ({setHeaderName, history }: {setHeaderName?: (val:string)=>void, history?: History }) => {
      const [postCode,setPostCode] = React.useState("");

       //Custom hook to fetch data
      const { data } = useFetch(`https://api.postcodes.io/postcodes/${postCode}/validate`);

      React.useEffect(()=> {
        if(!setHeaderName)
         return;
        setHeaderName("Postcode Finder");
    },[setHeaderName])

      const handleChange = (e:any) => {
        setPostCode(e.target.value);
      }

      const onSubmit = (e:any) => {
        e.preventDefault();
        if(history) {
          const appendPath = history.location.pathname.endsWith("/")?`${postCode}`:`/${postCode}`;
          alert('Path='+ history.location.pathname);
          alert('Append path='+ appendPath);
          history?.push(`${history.location.pathname}${appendPath}`)  
        }

      }


        return (
          <div className={classes.fullscreenDiv} >
          <form onSubmit={onSubmit}>
            <div className={classes.center}>
              <label className={classes.padding} >
                <input type="text" placeholder= "Type a postcode" className={classes.textbox} value={postCode} onChange={handleChange} />
              </label>
              <input type="submit" disabled={!(data && "result" in  data && data["result"])} className={classes.button} value="Submit" />
           </div>
          </form>

          </div>
        );

}
