import React from 'react';
import { InfoBox } from '../../Components/InfoBox/InfoBox';
import { useFetch } from '../../hooks/useFetch';
import Wrapper from '../../utils/Wrapper';
import classes from './PostcodeDisplay.module.css';

export const PostcodeInfo = ({postcode}:{postcode:string}) => {

    //Custom hook to fetch data
    const { data, loading, error } = useFetch(`https://api.postcodes.io/postcodes/${postcode}`);
    const getData = (data: any): [] => {
        if (data && data.data && data.data.length > 0) return data.data;
        else return [];
    };

    const postcodeData = React.useMemo(() => getData(data), [data]);


    //Format data to display as content
    const formatContentData = (data: any) => {
        return {
            'Postcode': data.postcode,
            Publisher: data.country,
        };
    };

    //Create content
    const prepareContent = (data: any) => {
            const formattedData = formatContentData(data);
            const detailsArray: any[] = [];
            for (const [k, v] of Object.entries(formattedData)) {
                detailsArray.push([k, v]);
            }
            return (
                <ul>
                    {detailsArray.map((row, indx) => {
                        return (
                            <li key={indx.toString()}>
                                <h2>{row[0]} </h2>
                                <ul>
                                    {Array.isArray(row[1]) ? (
                                        row[1].map((val, i) => <li key={val + i}>{val}</li>)
                                    ) : indx === detailsArray.length - 1 ? (
                                        <li>
                                            <a href={row[1]}>{row[1]}</a>
                                        </li>
                                    ) : (
                                        <li>{row[1]}</li>
                                    )}
                                </ul>
                            </li>
                        );
                    })}
                </ul>
            );
    };
    return(
            <Wrapper>
                {loading ? 
                ( <>
                    <div className={classes.Loader} />
                    <div className={classes.ContainerText}>Loading</div>
                 </>
                )
                : Object.keys(error).length !== 0 ? (
                        <div> Error while fetching data</div>
                    ) : <InfoBox title={'Details'}>{prepareContent(postcodeData)}</InfoBox>}
            </Wrapper>
    );
}
