import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
//import Wrapper from '../../utils/Wrapper';
import classes from './ContractIDParagraphDisplay.module.css';

export const ContractParagraphDisplay = ({paragrap} : {setHeaderName?: (val:string)=>void}) => {
    const { id } = useParams<{ id: string }>();
    const [paragraphData, setParaGraphData] = useState<any>([]);
    const [caseData, setCaseData] = useState<any>({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState({});
    const [paragraphNumber,setParagraphNumber] = React.useState(0);
    const [paragraphURL,setParagraphURL] = React.useState("");
    const baseURL = "http://localhost:3000"

    const fetchData = async(apiUrl:string,isParagraphData:boolean) => {
        try {
            fetch(apiUrl)
                .then((res) => {
                    return res.json()
                })
                .then((data) => {
                    if(isParagraphData)
                    {
                      setParaGraphData((prevState:any) => [...prevState, data] );
                    }
                    else setCaseData(data);
                    setLoading(false);
                }).catch(error => {
                    setError(error);
                    setLoading(false);
                });
        } catch (err) {
            setError(err);
            setLoading(false);
        }
    }

   React.useEffect(() => {
        fetchData(`${baseURL}/contracts/${id}`,false);
    },[id])


    //Initialise header on mount
    React.useEffect(()=> {
        if(Object.keys(caseData).length<=0)
         return;
        if(!setHeaderName)
         return;
        setHeaderName(caseData.data.attributes["name"]);
        setParagraphURL(caseData.relationships.paragraphs.links.self);
        setParagraphNumber(1);
    },[caseData, setHeaderName])

    React.useEffect(()=> {
        if(paragraphNumber<=0)
        return;
        const paraGraphURL= `${baseURL}${paragraphURL}?page=${paragraphNumber}&pageSize=50`
        fetchData(paraGraphURL,true);
    },[paragraphNumber, id, paragraphURL])

    const formatParagraphs = ()=> {
        const data =  paragraphData.map((val:any)=> {
            const ret = val.data.attributes.text;
            return ret;
        }) 
        return data;  
    }

    return (

            <div className={classes.fullscreenDiv} >
                {paragraphData.length>0 && formatParagraphs()}
            </div>
    );
};
