import React from 'react';
import './App.css';
import Layout from './Layout/Layout';
import {
    BrowserRouter as Router, Route, Switch,
  } from "react-router-dom";
import { PostcodeForm } from './pages/Form/PostcodeForm';
import { PostcodeDisplay } from './pages/Display/PostcodeDisplay';

interface RouteProps {
    path: string;
    children: React.ReactElement;
}  
const AppRoute = (props: RouteProps) => {
    return  (
      <Route
        exact
        path={props.path}
        render={(routeProps) =>
          React.cloneElement(props.children, { ...routeProps })
        }
      />
    ) 
};
function App() {
    return (
        <Router>  
            <Switch>
                <AppRoute path="/postcode-app/:id">
                    <Layout>
                        <PostcodeDisplay/>
                    </Layout>
                </AppRoute>
                <AppRoute path="/postcode-app">
                    <Layout>
                        <PostcodeForm />
                    </Layout>
                </AppRoute>
            </Switch>
        </Router>
    );
}
 //render={(props) => (<Component {...props} />)
export default App;
