import React from "react";
import { Url } from "url";
import { InfoBox } from "../Components/InfoBox/InfoBox";
import { Table } from "../Components/Table/Table";
import Aux from "../hoc/Aux";
import { useFetch } from "../hooks/useFetch";
import classes from "./DataBuilder.module.css"


export const DataBuilder = () => {

  const [selectedDataIndex,setSelectedDataIndex] = React.useState(0);

  //Custom hook to fetch data
  const {data,loading,error} =   useFetch(`https://api.test.datacite.org/dois?query=prefix:10.5517`);


  const getData = (data:any) : [] => {
    if(data && data.data && data.data.length>0)
      return data.data;
    else
      return [];  
  }

  const ccdData = React.useMemo(() => getData(data),[data]);

  //Create table structure 
  const prepareSummaryTableColumns = () => {
        return [
          {
            Header: () => "Summary",
            id: "axes-header",
            columns: ["id", "title"].map((th, idx) => {
              return {
                Header: idx===0?th.toUpperCase() :th.charAt(0).toUpperCase() + th.slice(1),
                accessor: th,
              };
            }),
          },
        ];
      };


   const formatHeaderData = (data:any)=> {
        return data.map((entry:any) => ({ id: entry.id, title: entry.attributes.titles[0].title}))
   }



   const formatContentData= (data:any)   => {
      return {
      "Publication Year" : data[selectedDataIndex].attributes.publicationYear, 
      Publisher: data[selectedDataIndex].attributes.publisher,
      Subjects: data[selectedDataIndex].attributes.subjects.map((val:any)=>val["subject"]),
      URL : data[selectedDataIndex].attributes.url}
   }

   const prepareContentdata = (data:any) => {
     if(selectedDataIndex >= 0 )
     {
      const formattedData = formatContentData(data);
      const detailsArray : any [] =[];
      for (const [k, v] of Object.entries(formattedData)) {
        detailsArray.push([k,v]);
      }
      return (
      <ul>
        {
            detailsArray.map((row,indx) => {
              return (
              <li>
                <h2>{row[0]} </h2>
                <ul>
                    { Array.isArray(row[1])
                       ? row[1].map(val=><li>{val}</li>) 
                       : indx===detailsArray.length-1 ? 
                         <li>
                           <a href={row[1]}>
                             {row[1]}
                           </a>
                         </li> 
                       : <li>{row[1]}</li>}
                </ul>
              </li> )
            })
        }
      </ul>)
     }
     else
     return "";

   }

   const handleTableRowClick = (indx:number) => {
    setSelectedDataIndex(indx);
   }

   return (
    <Aux>

        {loading?
        <>
         <div className= {classes.ContainerText}>
              Loading
          </div>
         <div className={classes.Loader}>
         </div>
        </> 
        : ccdData.length>0 
        ? 
        Object.keys(error).length!== 0 ?
          <div> Error while fetching data</div>
        : <div className= {classes.Conatiner}>
            <div className = {classes.Left} > 
                <Table 
                  columns={prepareTableOverviewColumns()} 
                  data={formatHeaderData(ccdData)} 
                  selectedRow={0} 
                  pageIndexValue ={0} 
                  pageSizeValue={20} 
                  onRowClick={handleTableRowClick}
                  /> 
            </div> 
            <div className = {classes.Right} >
            <InfoBox title={"Details"}>{prepareContentdata(ccdData)}</InfoBox>

            </div>   
        </div>
        : "" }
    </Aux>
    )
}
