  const [loading,setLoading] = React.useState(false);
  const [data,setData] = React.useState({});

 const formatData = (data: any): StructuralData => 
  {
      return { title: data.title, id: data.id }
  }
   React.useEffect(() => {
    const apiUrl = `https://api.test.datacite.org/dois?query=prefix:10.5517`;

    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => {
        setLoading(false);
        console.log(data);
        setData(data.data.map((val:any)=>formatData(val)));
      });
  }, []);

