import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useFetch } from '../../hooks/useFetch';
//import Wrapper from '../../utils/Wrapper';
import classes from './ContractDisplay.module.css';
import { ContractParagraphDisplay } from './ContractParagraphDisplay';

type CaseDataType = {
    caseID: string;
    paragraphURL:string;
}

export const ContractDisplay = ({setHeaderName} : {setHeaderName?: (val:string)=>void}) => {
    const { id } = useParams<{ id: string }>();
    const [contractData, setContractData] = useState<any>();
    const [paragraphURL, setParagraphURL] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState({});
    const baseURL = "http://localhost:3000"

    const fetchData = async(apiUrl:string) => {
        try {
            fetch(apiUrl)
                .then((res) => {
                    return res.json()
                })
                .then((data) => {
                    setContractData(data);
                    setLoading(false);
                }).catch(error => {
                    setError(error);
                    setLoading(false);
                });
        } catch (err) {
            setError(err);
            setLoading(false);
        }
    }
   const {data} = useFetch(`${baseURL}/contracts/${id}`) 

   React.useEffect(() => {
       try {
        fetchData(`${baseURL}/contracts/${id}`);
       }
       catch(err) {
           console.log(err);
       }
    },[id])

    React.useEffect(() => {
       if(contractData && (Object.keys(contractData).length>=0))
       {
        if(setHeaderName)
            setHeaderName(contractData.data.attributes["name"]);
        setParagraphURL(contractData.relationships.paragraphs.links.self);
       }
     },[contractData, setHeaderName])

    return (

            <div className={classes.fullscreenDiv} >
                { paragraphURL.length>0 && <ContractParagraphDisplay paragraphURL={paragraphURL}/>}
            </div>
    );
};
