import React from 'react';
import { Table } from '../../Components/Table/Table';
import { useFetch } from '../../hooks/useFetch';
import Wrapper from '../../utils/Wrapper';
import { getPostcodeData, PostCodeInfo } from './PostcodeDisplay';
import classes from './PostcodeDisplay.module.css';

export const PostcodeNearestInfo = ({postcode}: {postcode:string}) => {

    const { data, loading, error } = useFetch(`https://api.postcodes.io/postcodes/${postcode}/nearest`);

    const getPostcodeData = (data:any) => {
        if (data && data.result)  {
            return data.result.map((val:any)=> {
                return {
                    postcode: val.postcode,
                    region: val.region,
                    country : val.country,
                }
            })
       }
       else return [];
    }

    const postcodeData = React.useMemo(() => getPostcodeData(data), [data]);

    //Create table structure
    const prepareSummaryTableColumns = () => {
        const width:string[] = ["15%","15%","15%"];
        return [
            {
                Header: () => 'Nearest postcodes',
                id: 'axes-header',
                columns: ['postcode','region', 'country'].map((th, idx) => {
                    return {
                        Header:  th.charAt(0).toUpperCase() + th.slice(1),
                        accessor: th,
                        style: {
                            width: width[idx]
                            },
                    };
                }),
            },
        ];
    };

    //Fomat data to display as summary
    const formatSummaryData = (data: PostCodeInfo[]) => {
        return data.filter((val)=>val.postcode !== postcode).map((entry: any) => ({ postcode: entry.postcode, region: entry.region, country:entry.country }));
    };  

    return (
        <Wrapper>
                {loading ? 
                ( <>
                    <div className={classes.Loader} />
                    <div className={classes.ContainerText}>Loading</div>
                 </>
                )
                : Object.keys(error).length !== 0 ? (
                        <div> Error while fetching data</div>
                    ) :  <Table
                    columns={prepareSummaryTableColumns()}
                    data={formatSummaryData(postcodeData)}
                    pageIndexValue={0}
                    pageSizeValue={20}
                />}
            </Wrapper>

    )
}
