import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
//import Wrapper from '../../utils/Wrapper';
import classes from './ContractDisplay.module.css';

export const ContractParagraphDisplay = ({paragraphURL} : {paragraphURL: string}) => {
    const { id } = useParams<{ id: string }>();
    const [paragraphData, setParaGraphData] = useState<any>([]);
    const [caseData, setCaseData] = useState<any>({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState({});
    const [paragraphNumber,setParagraphNumber] = React.useState(1);
    const baseURL = "http://localhost:3000"

    const fetchData = async(apiUrl:string) => {
        try {
            fetch(apiUrl)
                .then((res) => {
                    return res.json()
                })
                .then((data) => {
                    setParaGraphData((prevState:any) => [...prevState, data] );
                    setLoading(false);
                }).catch(error => {
                    setError(error);
                    setLoading(false);
                });
        } catch (err) {
            setError(err);
            setLoading(false);
        }
    }


    React.useEffect(()=> {
        if(paragraphNumber<=0)
        return;
        const paraGraphURL= `${baseURL}${paragraphURL}?page=${paragraphNumber}&pageSize=50`
        fetchData(paraGraphURL);
    },[paragraphNumber, id, paragraphURL])

    const formatParagraphs = ()=> {
        const data =  paragraphData.map((val:any)=> {
            const ret = val.data.attributes.text;
            return ret;
        }) 
        return data;  
    }

    return (

            <div className={classes.fullscreenDiv} >
                {paragraphData.length>0 && formatParagraphs()}
            </div>
    );
};
