import React from "react";
import { Table } from "../Components/Table";
import Aux from "../hoc/Aux";
import { useFetch } from "../hooks/useFetch";
import { StructuralData } from "./SDInterface";

export const DataBuilder = () => {

 const {loading,error} =   useFetch(`https://api.test.datacite.org/dois?query=prefix:10.5517`);

  const prepareTableColumns = () => {
        return [
          {
            Header: () => "Crystal Structure information",
            id: "axes-header",
            columns: ["ID", "Title"].map((th, idx) => {
              return {
                Header: th,
                accessor: "col" + idx,
              };
            }),
          },
        ];
      };
   const formatData = (data:any)=> {
       return data.data.map((entry:any) => ({ id: entry.id, title: data.attributes.titles[0].title}))
   }

    return (
        <Aux>
            {data && data.data && !loading ? <Table columns={prepareTableColumns} data={formatData(data)}/> : ""}
        </Aux>
        )
}
