import React from "react";
import { StructuralData } from "./SDInterface";

export const DataBuilder = () => {

  const [loading,setLoading] = React.useState(false);
  const [data,setData] = React.useState<StructuralData[]|undefined>([]);

 const formatData = (data: any): StructuralData => 
  {
      return { title: data.title, id: data.id }
  }
   React.useEffect(() => {
    setLoading(true);
    const apiUrl = `https://api.test.datacite.org/dois?query=prefix:10.5517`;
    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => {
        setLoading(false);
        setData(data);
      });
  }, []);

  const List = (data:any[]) => {
    if (!data || data.length === 0) return <p>No repos, sorry</p>;
    return (
      <ul>
        <h2 className='list-head'>Available Public Repositories</h2>
        {data.map((dataVal) => {
          return (
            <li key={dataVal.id} className='list'>
              <span className='repo-text'>{repo.name} </span>
              <span className='repo-description'>{repo.description}</span>
            </li>
          );
        })}
      </ul>
    );
  };


    return (
        <div>

        {<List data={data}/>}
        </div>
        )
}
