import React from 'react';
import { useTable, usePagination, Row } from 'react-table';
import classes from './Table.module.css';

//Function component to display a table
export const Table = ({
    columns,
    data,
    pageIndexValue,
    pageSizeValue,
}: {
    columns: any;
    data: any;
    pageIndexValue: number;
    pageSizeValue: number;
}) => {
    const {
        // Table - instance properties:
        getTableProps,
        getTableBodyProps,
        headerGroups,
        prepareRow, // Pagination - instance properties:
        // @ts-ignore
        page, // Instead of using `rows`, we'll use `page`, which has only the rows for the active page
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        // Final state object of table:
        state: { pageIndex, pageSize },
        // @ts-ignore
    } = useTable(
        {
            columns,
            data,
            initialState: { pageIndex: pageIndexValue, pageSize: pageSizeValue },
        },
        usePagination,
    );


    // Render the UI for your table
    return (
        <>
            {/* from https://react-table.tanstack.com/docs/examples/pagination
        Pagination can be built however you'd like. 
        This is just a very basic UI implementation:
      */}

            <div className={classes.Table}>
                <table {...getTableProps()}>
                    <thead className={classes.TableHeader}>
                        {headerGroups.map((headerGroup: any) => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map((column: any) => (
                                    <th style={column.style} {...column.getHeaderProps()} className={classes.TableHeader}>
                                        {column.render('Header')}
                                    </th>
                                ))}
                            </tr>
                        ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {page.map((row: Row<object>, indx: number) => {
                            prepareRow(row);
                            return (
                                <tr
                                    {...row.getRowProps()}
                                        className={classes.TableRow
                                    }
                                >
                                    {row.cells.map((cell, indx) => {
                                        return (
                                            <td {...cell.getCellProps()} className={classes.TableCell}>
                                                {cell.render('Cell')}
                                            </td>
                                        );
                                    })}
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
             { pageCount>1 &&
                <div className={classes.TablePagination}>
                    <button
                        className={classes.PaginationButtonSpace}
                        onClick={() => gotoPage(0)}
                        disabled={!canPreviousPage}
                    >
                        {'<<'}
                    </button>{' '}
                    <button
                        className={classes.PaginationButtonSpace}
                        onClick={() => previousPage()}
                        disabled={!canPreviousPage}
                    >
                        {'<'}
                    </button>{' '}
                    <button className={classes.PaginationButtonSpace} onClick={() => nextPage()} disabled={!canNextPage}>
                        {'>'}
                    </button>{' '}
                    <button
                        className={classes.PaginationButtonSpace}
                        onClick={() => gotoPage(pageCount - 1)}
                        disabled={!canNextPage}
                    >
                        {'>>'}
                    </button>{' '}
                    <span className={classes.PaginationButtonSpace}>
                        Page{' '}
                        <strong>
                            {pageIndex + 1} of {pageOptions.length}
                        </strong>{' '}
                    </span>
                    <span>
                        | Go to page:{' '}
                        <input
                            type="number"
                            defaultValue={pageIndex + 1}
                            onChange={(e) => {
                                const page = e.target.value ? Number(e.target.value) - 1 : 0;
                                gotoPage(page);
                            }}
                            style={{ width: '100px' }}
                        />
                    </span>{' '}
                    <select
                        value={pageSize}
                        onChange={(e) => {
                            setPageSize(Number(e.target.value));
                        }}
                        className={classes.PaginationButtonSpace}
                    >
                        {[10, 20, 30, 40, 50].map((pageSize) => (
                            <option key={pageSize} value={pageSize}>
                                Show {pageSize}
                            </option>
                        ))}
                    </select>
                </div>
            }
        </>
    );
};
