import React from 'react';
import { useParams } from 'react-router-dom';
import Wrapper from '../../utils/Wrapper';
import classes from './PostcodeDisplay.module.css';
import { PostcodeInfo } from './PostcodeInfo';
import { PostcodeNearestInfo } from './PostcodeNearestInfo';

export type PostCodeInfo = {postcode:string,region:string,country:string}
export const formatPostcodeData = (val: any): PostCodeInfo => {
        return {
            postcode: val.postcode,
            region: val.region,
            country : val.country,
        }
};

export const PostcodeDisplay = ({setHeaderName} : {setHeaderName?: (val:string)=>void}) => {
    const { id } = useParams<{ id: string }>();

    //Initialise header on mount
    React.useEffect(()=> {
        if(!setHeaderName)
         return;
        const headerName = id.toUpperCase().replace(/^(.{3})(.*)$/, "$1 $2");
        setHeaderName(headerName);
    },[id, setHeaderName])
    return (
            <Wrapper>
            {
                <div className={classes.Conatiner}>
                    <div className={classes.Left}>
                        <PostcodeInfo postcode={id}/>
                    </div>
                    <div className={classes.Right}>
                        <PostcodeNearestInfo postcode={id}/>
                    </div>
                </div>
            }
            </Wrapper>
    );
};
