import React from "react";

export const DataBuilder = () => {

   const [fetchState,setFetchState] = React.useState({loading:false,data:[{}]});

   React.useEffect(() => {
    setFetchState({loading:true,data:[{}]});
    const apiUrl = `https://api.test.datacite.org/dois?query=prefix:10.5517`;
    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => {
        setFetchState({data:data,loading:false});
      });
  }, [setFetchState]);

  const List = (data:[{}]) => {
    const { repos } = data;
    if (!repos || repos.length === 0) return <p>No repos, sorry</p>;
    return (
      <ul>
        <h2 className='list-head'>Available Public Repositories</h2>
        {repos.map((repo) => {
          return (
            <li key={repo.id} className='list'>
              <span className='repo-text'>{repo.name} </span>
              <span className='repo-description'>{repo.description}</span>
            </li>
          );
        })}
      </ul>
    );
  };


    return (
        <div>
        {!fetchState.loading? <List props={fetchState.data}/>:""}
        </div>
        )
}
