import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { ParagraphComponent } from '../../Components/InfoBox/ParagraphComponent';
import { useFetch } from '../../hooks/useFetch';
//import Wrapper from '../../utils/Wrapper';
import classes from './ContractParagraphDisplay.module.css';

export const ContractParagraphDisplay = ({paragraphURL} : {paragraphURL: string}) => {
    const [paragraphData, setParaGraphData] = useState<any>([]);
   const [paragraphNumber,setParagraphNumber] = React.useState(1);
    const baseURL = "http://localhost:3000"
    const componentRef = React.useRef<HTMLDivElement>(null);

    const {data,loading,error} = useFetch(`${baseURL}${paragraphURL}?page=${paragraphNumber}&pageSize=50`) 

    /*const fetchData = async(apiUrl:string) => {
        try {
            fetch(apiUrl)
                .then((res) => {
                    return res.json()
                })
                .then((data) => {
                    if(Object.keys(data).length!==0 && "data" in data)
                        setParaGraphData((prevState:any) => [...prevState, data] );
                    setLoading(false);
                }).catch(error => {
                    setError(error);
                    setLoading(false);
                });
        } catch (err) {
            setError(err);
            setLoading(false);
        }
    }*/
    React.useEffect(()=> {
        window.addEventListener('scroll', infiniteScroll);
    },[])

    React.useEffect(()=> {
        if(Object.keys(data).length!==0 && "data" in data)
          setParaGraphData((prevState:any) => [...prevState, data] );
    },[data])

   /* React.useEffect(()=> {
        if(paragraphNumber<=0)
            return;
        const paraGraphURL= `${baseURL}${paragraphURL}?page=${paragraphNumber}&pageSize=50`
        fetchData(paraGraphURL);
    },[paragraphNumber, id, paragraphURL])*/

    React.useLayoutEffect(()=> {
        if(paragraphData.length ===0)
        return;
        if(componentRef.current && window.innerHeight > componentRef.current.scrollHeight)
            setParagraphNumber(prev=>prev+1);
    },[paragraphData])

    const displayParagraphs = ()=> {
        const retComponentArr: any[] =[]
          paragraphData.forEach((val:any)=> {
            if(val.data || val.data.attributes)
                retComponentArr.push( <ParagraphComponent content={val.data.attributes.text?val.data.attributes.text:""}/>)
        })
        return retComponentArr;
    }
    const infiniteScroll = () => {
        // End of the document reached?componentRef.current.scrollHeight
        if(componentRef.current && componentRef.current.getBoundingClientRect().bottom === window.innerHeight)
            setParagraphNumber((prev)=>prev+1);
    }

    return (

            <div className={classes.fullscreenDiv} ref = {componentRef}>
                {paragraphData.length>0 && displayParagraphs()}
            </div>
    );
};
